INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (164724, 'Selegiline', 'Pentoxyverine', 'Major', 'Labelings for products containing carbetapentane state that the drug should not be used in combination with monoamine oxidase inhibitors (MAOIs). No data are available regarding a potential interaction. However, serious and fatal reactions have been associated with another cough suppressant, dextromethorphan, during coadministration with MAOIs. Dextromethorphan is a weak serotonin reuptake inhibitor whose serotonergic activity may be enhanced by MAOIs. The result is an increased risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A receptors and characterized by symptoms such as CNS irritability, altered consciousness, confusion, myoclonus, ataxia, abdominal cramping, hyperpyrexia, shivering, pupillary dilation, diaphoresis, hypertension, and tachycardia.', 'DDInter', 'Products containing carbetapentane should not be used concurrently with MAOIs or for 14 days after stopping treatment with an MAOI.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/164897/', '', 'Apomorphine, Amantadine, Cabergoline', 'Benzonatate', 1767369485);
