INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (164849, 'Yohimbine', 'Selegiline', 'Moderate', 'Theoretically, yohimbine may precipitate severe hypertensive reactions and hyperpyrexia in patients treated with monoamine oxidase inhibitors (MAOIs). Yohimbine has been shown to induce the release of norepinephrine in patients with essential hypertension. This action could produce a synergistic sympathomimetic effect with MAOIs, which enhance norepinephrine storage in adrenergic neurons.', 'DDInter', 'While clinical data are lacking, yohimbine should preferably not be used with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with yohimbine.', 'Others', 'While clinical data are lacking, yohimbine should preferably not be used with MAOIs or other agents that possess MAOI activity (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/165022/', '', 'Apomorphine, Ropinirole, Opicapone, Cabergoline, Amantadine, Entacapone, Tolcapone, Rotigotine, Bromocriptine, Pergolide, Pramipexole, More', 'Apomorphine, Fesoterodine, Pentosan polysulfate, Calcium chloride, Tolterodine, Phenazopyridine, Trospium, Mirabegron, Alprostadil, Acetohydroxamic acid, Darifenacin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (164850, 'Zolmitriptan', 'Selegiline', 'Moderate', 'Coadministration of selegiline with 5-HT1 receptor agonists may be associated with a theoretical risk of serotonin syndrome. The proposed mechanism involves an additive pharmacodynamic effect resulting from inhibition of serotonin metabolism and stimulation of 5-HT1A receptors.', 'DDInter', 'Caution is advisable when 5-HT1 receptor agonists are used with selegiline, particularly at dosages higher than that recommended for the treatment of Parkinson''s disease. Patients should be monitored for symptoms of the serotonin syndrome, which may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea. If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately and supportive care rendered as necessary. Moderately ill patients may also benefit from the administration of a serotonin antagonist (e.g., cyproheptadine, chlorpromazine). Severe cases should be managed under consultation with a toxicologist and may require sedation, neuromuscular paralysis, intubation, and mechanical ventilation in addition to the other measures.', 'Metabolism, Synergism', 'Caution is advisable when 5-HT1 receptor agonists are used with selegiline, particularly at dosages higher than that recommended for the treatment of Parkinson''s disease.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/165023/', '', 'Rimegepant, Eptinezumab, Ubrogepant, Galcanezumab, Atogepant, Methysergide, Erenumab, Fremanezumab', 'Apomorphine, Ropinirole, Opicapone, Amantadine, Rasagiline, Entacapone, Tolcapone, Rotigotine, Pergolide, Safinamide, Levodopa', 1767369485);
