INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (165276, 'Ethotoin', 'Sertraline', 'Moderate', 'Two case reports suggest that sertraline may significantly increase phenytoin levels when used concomitantly. The mechanism is thought to be related to inhibition of CYP450 2C9-mediated metabolism of phenytoin by sertraline. Other hydantoins may also be affected. In addition, coadministration of sertraline with potent inducers of CYP450 3A4, such as phenytoin, may result in a reduction in the plasma concentrations and therapeutic effects of sertraline. It cannot be excluded that other CYP450 3A4 inducers (e.g., carbamazepine, phenobarbital (phenobarbitone), rifampin, St. John''s wort) may also cause a reduction of sertraline plasma levels.', 'DDInter', 'Clinical and laboratory monitoring for increased hydantoin levels and toxicity is recommended whenever sertraline is added or dosage is changed. Patients should be advised to notify their doctor if they experience symptoms of toxicity (e.g., drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia). In addition, the potential for diminished therapeutic effects of sertraline should be considered when prescribed in combination with phenytoin. Patients should be closely monitored, and the dosage of sertraline adjusted as necessary. Data are not available to guide dosing of sertraline when coadministered with potent CYP450 3A4 inducers. Alternative agents with no or minimal CYP450 3A4-inducing activity may also be considered.', 'Metabolism', 'Clinical and laboratory monitoring for increased hydantoin levels and toxicity is recommended whenever sertraline is added or dosage is changed.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/165449/', '', 'Oxitriptan, Bupropion, Viloxazine, Tryptophan', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (165277, 'Etodolac', 'Sertraline', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', 'DDInter', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/165450/', '', 'Chondroitin sulfate, Esomeprazole, Glucosamine', 'Phenelzine, Desipramine, Tranylcypromine, Trimipramine, Maprotiline, St. John''s Wort, Isocarboxazid, Oxitriptan, Viloxazine, Tryptophan, Amoxapine, More', 1767369485);
