INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (165495, 'Felodipine', 'Sevoflurane', 'Moderate', 'Calcium channel blockers may potentiate the cardiac depressant and hypotensive effects of volatile (halogenated) anesthetics, and vice versa, due to their similar pharmacologic action on the cardiovasculature. Serious consequences of this interaction may include cardiovascular collapse, ventricular impairment, and/or conduction disturbances, particularly in patients with underlying cardiac complications. The interaction may be more likely with verapamil and diltiazem due to their dromotropic and chronotropic effects.', 'DDInter', 'Discontinuation of calcium channel blockers prior to surgery is generally not recommended because of the rebound effect in patients with coronary heart disease or arterial hypertension. However, extreme care must be taken when administering halogenated anesthetics to patients under treatment with calcium channel blockers. Hemodynamic and electrocardiographic monitoring are recommended during the operation and postoperatively, and the dosage of one or both drugs adjusted as necessary.', 'Synergism', 'Discontinuation of calcium channel blockers prior to surgery is generally not recommended because of the rebound effect in patients with coronary heart disease or arterial hypertension.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/165668/', '', 'Acetylsalicylic acid, Nebivolol, Bisoprolol, Atenolol, Clevidipine, Celecoxib, Enalapril, Moexipril, Lisinopril, Perindopril, Bisoprolol, More', 'Thiopental, Remifentanil, Nitrous oxide, Methohexital, Esketamine, Etomidate, Ketamine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (165496, 'Fingolimod', 'Sevoflurane', 'Major', 'Due to its significant bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of fingolimod treatment in patients receiving drugs that prolong the QT interval. Fingolimod can cause a decrease in heart rate during initiation of therapy that is apparent within an hour of the first dose and maximal at approximately 6 hours postdose in most cases, but occasionally up to 20 hours after the first dose.', 'DDInter', 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, close monitoring is recommended during initiation of fingolimod treatment in patients receiving concomitant drugs that can prolong the QT interval, patients with significant QT prolongation (QTc >470 msec in females or >450 msec in males), or patients with relevant risk factors for QT prolongation (e.g., hypokalemia, hypomagnesemia, congenital QT prolongation).', 'Synergism', 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/165669/', '', 'Thiopental, Alfentanil, Remifentanil, Isoflurane, Sodium oxybate, Methohexital, Fentanyl, Desflurane, Nitrous oxide, Sufentanil, Enflurane, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, More', 1767369485);
