INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (165591, 'Anisindione', 'Sildenafil', 'Moderate', 'Coadministration of sildenafil with oral vitamin K antagonists in patients with pulmonary arterial hypertension has been associated with an increased risk of bleeding (primarily epistaxis) compared to administration without oral vitamin K antagonists. The incidence of epistaxis was 9% with oral vitamin K antagonists versus 2% with placebo.', 'DDInter', 'Caution is advised in patients with pulmonary arterial hypertension receiving sildenafil and oral vitamin K antagonists. The INR should be checked frequently and patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised in patients with pulmonary arterial hypertension receiving sildenafil and oral vitamin K antagonists.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/165764/', '', 'Tamsulosin, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Clindamycin, Trichlormethiazide, Ciclopirox, Vemurafenib, Probenecid, Tioconazole, More', '', 1767369485);
