INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (165800, 'Troglitazone', 'Silodosin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of silodosin, which is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 'The potential for diminished pharmacologic effects of silodosin should be considered during coadministration with CYP450 3A4 and/or P-gp inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of silodosin should be considered during coadministration with CYP450 3A4 and/or P-gp inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/165973/', '', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Tolazamide, Dulaglutide, Exenatide, Tolbutamide, Miglitol, More', 'Tamsulosin, Terazosin, Alfuzosin, Finasteride, Dutasteride', 1767369485);
