INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (165986, 'Telaprevir', 'Simvastatin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 including telaprevir may significantly increase the plasma concentrations of certain HMG-CoA reductase inhibitors and their pharmacologically active metabolites that are primarily metabolized by the isoenzyme.', 'DDInter', 'Due to the potential for severe interaction, concomitant use of atorvastatin, lovastatin, or simvastatin with telaprevir is considered contraindicated. Red yeast rice, which contains lovastatin, should also be avoided during treatment with telaprevir. Fluvastatin, pravastatin, and pitavastatin may be safer alternatives, since they are not metabolized by CYP450 3A4. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Due to the potential for severe interaction, concomitant use of atorvastatin, lovastatin, or simvastatin with telaprevir is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166159/', '', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Tolazamide, Empagliflozin, Dulaglutide, Exenatide, Tolbutamide, More', 'Paritaprevir, Tenofovir alafenamide, Cabotegravir, Maraviroc, Zanamivir, Rilpivirine, Dolutegravir, Brincidofovir, Elbasvir, Doravirine, Bictegravir, More', 1767369485);
