INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (166099, 'Idarubicin', 'Siponimod', 'Major', 'Due to its significant bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of siponimod treatment in patients receiving drugs that prolong the QT interval. Coadministration of siponimod with antineoplastic, immunosuppressive, or other immune-modulating therapies may increase the risk of unintended additive immunosuppressive effects. Siponimod causes reversible sequestration of lymphocytes in lymphoid tissues. When administered daily, siponimod produces a dose-dependent reduction in peripheral lymphocyte count to 20% to 30% of baseline values, which may increase the risk of infections.', 'DDInter', 'Siponimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, treatment with siponimod should generally not be initiated in patients who are concurrently treated with QT prolonging drugs with known arrhythmogenic properties. Advice from a cardiologist should be sought if treatment with siponimod is considered in patients on concurrent therapy with QT prolonging drugs with a known risk of torsades de pointes or drugs that slow heart rate or AV conduction. The safety and efficacy of siponimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated. Caution is advised during coadministration and for 3 to 4 weeks after the last dose of siponimod. When switching from drugs with prolonged immune effects to siponimod, the half-life and mode of action of these drugs must be considered to avoid unintended additive immunosuppressive effects while at the same time minimizing risk of disease reactivation.', 'Synergism', 'Siponimod has not been studied in patients receiving drugs that can prolong the QT interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166272/', '', 'Valrubicin', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (166100, 'Idelalisib', 'Siponimod', 'Major', 'Coadministration with drugs that cause moderate inhibition of CYP450 2C9 and moderate or strong inhibition of CYP450 3A4 may increase the plasma concentrations of siponimod, which is primarily metabolized by these isoenzymes. This interaction includes concomitant use of siponimod with a moderate CYP450 2C9/3A4 dual inhibitor or a moderate CYP450 2C9 inhibitor in combination with a separate moderate or strong CYP450 3A4 inhibitor.', 'DDInter', 'Concomitant use of siponimod with a moderate or strong CYP450 2C9/3A4 dual inhibitor (e.g., fluconazole, mifepristone) or a moderate CYP450 2C9 inhibitor (e.g., abiraterone, amiodarone, fenofibrate, gemfibrozil, nitisinone, oxandrolone) in combination with a moderate or strong CYP450 3A4 inhibitor (e.g., amprenavir, aprepitant, atazanavir, boceprevir, ceritinib, chloramphenicol, ciprofloxacin, clarithromycin, cobicistat, conivaptan, crizotinib, dalfopristin-quinupristin, darunavir, delavirdine, diltiazem, dronedarone, erythromycin, fedratinib, fluvoxamine, fosamprenavir, fosaprepitant, fosnetupitant, fusidic acid, idelalisib, imatinib, indinavir, isavuconazole, itraconazole, ketoconazole, letermovir, mibefradil, nefazodone, nelfinavir, netupitant, posaconazole, ribociclib, ritonavir, saquinavir, stiripentol, telaprevir, telithromycin, troleandomycin, verapamil, voriconazole, voxelotor) is not recommended. Caution is advised if siponimod is used in combination with moderate CYP450 2C9 inhibitors.', 'Metabolism', 'Concomitant use of siponimod with a moderate or strong CYP450 2C9/3A4 dual inhibitor (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166273/', '', 'Dacomitinib, Tepotinib, Selumetinib, Capmatinib, Afatinib, Tivozanib, Axitinib, Ripretinib, Regorafenib, Erlotinib, Neratinib, More', 'Antithymocyte immunoglobulin (rabbit), Secukinumab, Ravulizumab, Mycophenolic acid, Abatacept, Apremilast, Inebilizumab, Belumosudil, Thalidomide, Belimumab, Emapalumab, More', 1767369485);
