INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (166154, 'Siponimod', 'Ritodrine', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166327/', '', 'Bremelanotide, Flibanserin, Cabergoline, Bromocriptine, Naproxen, Ibuprofen', 'Golimumab, Cyclosporine, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (166155, 'Siponimod', 'Rubella virus vaccine', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during siponimod therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Siponimod causes reversible sequestration of lymphocytes in lymphoid tissues.', 'DDInter', 'The use of live attenuated vaccines should be avoided during and for up to 4 weeks after siponimod treatment, and siponimod should be paused 1 week prior to and until 4 weeks after a planned vaccination. Patients without a healthcare professional confirmed history of varicella or without documentation of a full course of vaccination against varicella-zoster virus (VZV) should be tested for antibodies to VZV before initiating treatment with siponimod. For antibody-negative patients, a full course of vaccination with varicella vaccine is recommended prior to treatment with siponimod. In patients who have recently been vaccinated, siponimod therapy should be postponed for 4 weeks to allow the full effect of vaccination to occur.', 'Others', 'The use of live attenuated vaccines should be avoided during and for up to 4 weeks after siponimod treatment, and siponimod should be paused 1 week prior to and until 4 weeks after a planned vaccination.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166328/', '', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', '', 1767369485);
