INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (166899, 'Indacaterol', 'Sotalol', 'Major', 'Beta-blockers may antagonize the effects of beta-2 adrenergic bronchodilators and precipitate acute, life-threatening bronchospasm in patients with asthma or other obstructive airway diseases. The mechanism involves increased airway resistance and reduced bronchodilation due to blockade of beta-2 adrenergic receptors. The interaction may also occur with ophthalmically applied beta-blockers, which are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Due to opposing effects on beta-2 adrenergic receptors, propranolol has been used in the treatment of albuterol overdose. Likewise, beta-2 adrenergic agonists may interfere with the pharmacologic effects of beta-blockers.', 'DDInter', 'Concomitant use of beta-2 adrenergic bronchodilators with beta-blockers, including ophthalmic formulations, should generally be avoided. If coadministration is required, a cardioselective beta-blocker (e.g., acebutolol, atenolol, betaxolol, bisoprolol, metoprolol, nebivolol) is usually preferred. Nevertheless, caution is advised and respiratory status should be closely monitored, as cardioselectivity is not absolute and larger doses of beta-1 selective agents may pose some of the same risks as nonselective agents. In general, nonselective beta-blockers are considered contraindicated in patients with obstructive airways disease.', 'Antagonism', 'Concomitant use of beta-2 adrenergic bronchodilators with beta-blockers, including ophthalmic formulations, should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167074/', '', 'Felodipine, Acetylsalicylic acid, Amlodipine', 'Mometasone furoate, Umeclidinium, Aclidinium, Mometasone, Cromoglicic acid, Ipratropium, Beclomethasone dipropionate', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (166900, 'Indapamide', 'Sotalol', 'Moderate', 'Although they are often combined in clinical practice, diuretics and beta-blockers may increase the risk of hyperglycemia and hypertriglyceridemia in some patients, especially in patients with diabetes or latent diabetes. In addition, the risk of QT interval prolongation and arrhythmias (e.g. torsades de pointes) due to sotalol may be increased by potassium-depleting diuretics.', 'DDInter', 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration. Patients should be advised to seek medical assistance if they experience dizziness, weakness, fainting, fast or irregular heartbeats, or loss of blood glucose control.', 'Synergism', 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167075/', '', 'Ivabradine', 'Moexipril, Bisoprolol, Benazepril, Quinapril, Trandolapril, Fosinopril, Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, More', 1767369485);
