INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (166961, 'Sotalol', 'Reserpine', 'Moderate', 'Sympathetic ganglion-blocking or catecholamine-depleting agents such as guanethidine, reserpine, and monoamine oxidase (MAO) inhibitors may potentiate the pharmacologic effects of beta-blockers, which are thought to competitively antagonize catecholamines at cardiac and other peripheral adrenergic neurons. Combining these medications may increase the risk of hypotension, orthostasis, bradycardia, and heart failure due to excessive reduction of sympathetic activity.', 'DDInter', 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents. Patients should contact their doctor if they experience dizziness, lightheadedness, syncope, bradycardia, shortness of breath, difficulty breathing, edema, and/or chest pain.', 'Synergism', 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167136/', '', 'Deserpidine, Deserpidine', 'Felodipine, Acetylsalicylic acid, Amlodipine, Ivabradine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (166962, 'Sotalol', 'Riociguat', 'Moderate', 'Riociguat may potentiate the action of antihypertensive agents and other vasodilators. Symptomatic hypotension may occur. Riociguat stimulates soluble guanylate cyclase (sGC), an enzyme in the cardiopulmonary system that binds with nitric oxide (NO) to catalyze the synthesis of cyclic guanosine monophosphate (cGMP). The resultant accumulation of cGMP in vascular smooth muscle cells leads to peripheral vasodilation and blood pressure decreases.', 'DDInter', 'Caution is advised if riociguat is prescribed in combination with antihypertensive agents or other vasodilators.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167137/', '', 'Acetylsalicylic acid, Ivabradine', 'Bosentan, Macitentan, Metyrosine', 1767369485);
