INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (167618, 'Triamcinolone (ophthalmic)', 'Sulindac', 'Moderate', 'The combined use of corticosteroids and nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration, and perforation.', 'DDInter', 'Caution is advised if corticosteroids and NSAIDs are used together, especially in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients. During concomitant therapy, patients should be advised to take the medications with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools. The selective use of prophylactic anti-ulcer therapy (e.g., antacids, H2-antagonists) may be considered.', 'Synergism', 'Caution is advised if corticosteroids and NSAIDs are used together, especially in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167794/', '', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole', 'Mometasone furoate, Amcinonide, Fluocinolone acetonide, Hydrocortisone butyrate, Fluocinonide, Fluorometholone, Clobetasol, Beclomethasone dipropionate, Ciclesonide, Mupirocin, Mometasone furoate, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (167619, 'Triamterene', 'Sulindac', 'Moderate', 'Concomitant use of nonsteroidal anti-inflammatory drugs (NSAIDs) and diuretics may adversely affect renal function due to NSAID inhibition of the renal synthesis of prostaglandins that help maintain renal perfusion in dehydrated states. The risk may be increased in patients on dietary sodium restriction. At the same time, hypotensive effect of the diuretics may be reduced because inhibition of prostaglandins can lead to unopposed pressor activity and, consequently, elevation in blood pressure. Natriuretic and diuretic effects may also be reduced, as NSAIDs have been reported to cause sodium and water retention, which may account for the increased risk of congestive heart failure associated with the combination. NSAIDs may also increase the risk of hyperkalemia associated with potassium-sparing diuretics.', 'DDInter', 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure. If renal insufficiency or hyperkalemia develops, both drugs should be discontinued until the condition is corrected.', 'Antagonism', 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167795/', '', 'Finerenone', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole', 1767369485);
