INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (167706, 'Indinavir', 'Sunitinib', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of sunitinib and its pharmacologically active metabolite, both of which are substrates of the isoenzyme. Because sunitinib is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 'Alternative agents with no or minimal CYP450 3A4-inhibiting activity are recommended in patients treated with sunitinib. Some authorities recommend avoiding concomitant use of sunitinib during and for 2 weeks after treatment with itraconazole. A dose reduction for sunitinib to a minimum of 37.5 mg daily for gastrointestinal stromal tumor (GIST) and metastatic renal cell carcinoma (mRCC) or 25 mg daily for pancreatic neuroendocrine tumors (pNET) should be considered if it must be coadministered with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', 'Metabolism', 'Alternative agents with no or minimal CYP450 3A4-inhibiting activity are recommended in patients treated with sunitinib.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167882/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, More', 'Futibatinib, Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Afatinib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (167707, 'Insulin human', 'Sunitinib', 'Moderate', 'The hypoglycemic effect of insulin may be potentiated by certain drugs, including ACE inhibitors, angiotensin receptor blockers (ARBs), 4-aminoquinolines, amylin analogs, anabolic steroids, fibrates, monoamine oxidase inhibitors (MAOIs, including linezolid), salicylates, selective serotonin reuptake inhibitors (SSRIs), sulfonamides, disopyramide, propoxyphene, quinidine, quinine, and ginseng. These drugs may increase the risk of hypoglycemia by enhancing insulin sensitivity (ACE inhibitors, ARBs, fibrates, ginseng); stimulating insulin secretion (salicylates, disopyramide, pentoxifylline, propoxyphene, quinidine, quinine, MAOIs, ginseng); decreasing insulin clearance and resistance (4-aminoquinolines); increasing peripheral glucose utilization (SSRIs, insulin-like growth factor); inhibiting gluconeogenesis (SSRIs, MAOIs, insulin-like growth factor); slowing the rate of gastric emptying (amylin analogs); and/or suppressing postprandial glucagon secretion (amylin analogs). Clinical hypoglycemia has been reported during use of some of these agents alone or with insulin and/or insulin secretagogues.', 'DDInter', 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin, particularly in patients with advanced age and/or renal impairment. The insulin dosage may require adjustment if an interaction is suspected. Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations).', 'Synergism', 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin, particularly in patients with advanced age and/or renal impairment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167883/', '', 'Liraglutide, Lixisenatide', 'Entrectinib, Imatinib, Pralsetinib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, More', 1767369485);
