INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (167782, 'Ziprasidone', 'Sunitinib', 'Major', 'Ziprasidone can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', 'DDInter', 'Coadministration of ziprasidone with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167958/', '', 'Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Brexpiprazole, Lurasidone', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Zanubrutinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (167783, 'Zoledronic acid', 'Sunitinib', 'Moderate', 'Osteonecrosis of the jaw (ONJ) has been reported with the use of angiogenesis inhibitors including denosumab, bevacizumab, and sunitinib. Concomitant exposure with other risk factors such as bisphosphonates (potency, route of administration, and cumulative dose), cancer, chemotherapy, corticosteroids, radiotherapy to the head and neck, dental disease, invasive dental procedures, or poor oral hygiene may increase the risk. Most reported cases with bevacizumab have occurred in patients who received prior or concomitant treatment with intravenous bisphosphonates or had a history of dental disease requiring invasive dental procedures.', 'DDInter', 'Caution is advised if bisphosphonates are used simultaneously or sequentially with denosumab or bevacizumab. Prior to initiating treatment, a thorough dental examination and appropriate preventive dentistry should be considered. If possible, invasive dental procedures should be avoided in patients who have previously received or are receiving intravenous bisphosphonates. Good oral hygiene practices should be maintained during treatment. Patients should be advised to seek medical attention if they experience signs and symptoms of osteonecrosis in the jaw, such as: pain in the mouth, teeth, or jaw, swelling or sores inside the mouth, numbness or a feeling of heaviness in the jaw, loosening of a tooth, or exposure of bone in the jaw. Temporary interruption of treatment should be considered if clinically appropriate, until the condition resolves.', 'Synergism', 'Caution is advised if bisphosphonates are used simultaneously or sequentially with denosumab or bevacizumab.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167959/', '', 'Cholecalciferol, Vosoritide, Denosumab, Burosumab', 'Entrectinib, Pralsetinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, More', 1767369485);
