INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (167895, 'Tazemetostat', 'Suvorexant', 'Moderate', 'Coadministration with CYP450 3A4 inhibitors may increase the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme. Clinically, this interaction may result in an increased risk of the frequency or severity of adverse reactions due to tazemetostat such as hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress. Data are not available for other, less potent inhibitors.', 'DDInter', 'Caution and clinical monitoring is recommended with the concomitant use of tazemetostat with CYP450 3A4 inhibitors. Patients should be advised to notify their health care professional if they experience signs or symptoms of possible tazemetostat-related adverse effects including secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', 'Metabolism', 'Caution and clinical monitoring is recommended with the concomitant use of tazemetostat with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/168071/', '', 'Temazepam, Remimazolam, Chloral hydrate, Nitrazepam, Thiopental, Quazepam, Paraldehyde, Propiomazine, Meprobamate, Scopolamine, Dexmedetomidine, More', 'Pertuzumab, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, More', 1767369485);
