INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (168019, 'Fosinopril', 'Tacrolimus', 'Moderate', 'Coadministration of tacrolimus with ACE inhibitors or angiotensin II receptor antagonists may increase the risk of hyperkalemia associated with the use of these drugs, particularly in patients with renal impairment.', 'DDInter', 'Serum potassium levels and renal function should be monitored regularly when tacrolimus is used in combination with ACE inhibitors or angiotensin II receptor antagonists. Particular caution is warranted in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', 'Serum potassium levels and renal function should be monitored regularly when tacrolimus is used in combination with ACE inhibitors or angiotensin II receptor antagonists.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/168195/', '', 'Indapamide', 'Minoxidil, Deoxycholic acid, Calcium gluconate, Magnesium sulfate, Glycopyrronium, Pimecrolimus, Aminobenzoic acid, Ivermectin, Caffeine, Cromoglicic acid, Abrocitinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (168020, 'Fosphenytoin', 'Tacrolimus', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein may significantly decrease the plasma concentrations and pharmacologic effects of tacrolimus. The mechanism probably involves reduced absorption as well as accelerated clearance of tacrolimus due to induction of both intestinal P-glycoprotein drug efflux transporter and hepatic/intestinal CYP450 3A4 isoenzymes.', 'DDInter', 'Given the risk of organ rejection associated with inadequate immunosuppressant levels, caution is advised if tacrolimus must be coadministered with potent inducers of CYP450 3A4 and/or P-gp. Tacrolimus blood levels should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of inducer therapy in patients who are stabilized on their anti-rejection regimen.', 'Metabolism, Absorption', 'Given the risk of organ rejection associated with inadequate immunosuppressant levels, caution is advised if tacrolimus must be coadministered with potent inducers of CYP450 3A4 and/or P-gp.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/168196/', '', 'Minoxidil, Deoxycholic acid, Calcium gluconate, Magnesium sulfate, Glycopyrronium, Pimecrolimus, Aminobenzoic acid, Finasteride, Ivermectin, Caffeine, Diclofenac, More', 'Ethotoin, Brivaracetam, Methsuximide, Paramethadione, Fenfluramine, Perampanel, Eslicarbazepine, Phensuximide, Ganaxolone, Phenacemide, Trimethadione, More', 1767369485);
