INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (168087, 'Tacrolimus', 'Magnesium hydroxide', 'Moderate', 'Coadministration with aluminum-magnesium hydroxide may increase tacrolimus blood concentrations in vivo. The mechanism of interaction is unknown.', 'DDInter', 'Given the narrow therapeutic index and the large interindividual variability in the pharmacokinetics of tacrolimus, caution is advised if the drug is used in combination with aluminum and/or magnesium hydroxide. Pharmacologic response and tacrolimus blood levels should be monitored more closely whenever aluminum/magnesium hydroxide is added to or discontinued from therapy, and the immunosuppressant dosage adjusted as necessary. Patients should be advised to contact their doctor if they experience potential signs and symptoms of tacrolimus toxicity such as fever, infection, diarrhea, tremor, headache, fatigue, lethargy, and changes in motor function, mental status, or sensory function.', 'Others', 'Given the narrow therapeutic index and the large interindividual variability in the pharmacokinetics of tacrolimus, caution is advised if the drug is used in combination with aluminum and/or magnesium hydroxide.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/168263/', '', 'Minoxidil, Deoxycholic acid, Pimecrolimus, Calcium gluconate, Glycopyrronium, Aminobenzoic acid, Finasteride, Ivermectin, Caffeine, Diclofenac, Cromoglicic acid, More', 'Fesoterodine, Pentosan polysulfate, Phenazopyridine, Acetohydroxamic acid, Trospium, Mirabegron, Yohimbine, Flavoxate, Darifenacin, Avanafil, Phenyl salicylate, More', 1767369485);
