INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (168141, 'Tipranavir', 'Tacrolimus', 'Moderate', 'Coadministration with tipranavir in combination with ritonavir may alter the pharmacokinetics of immunosuppressants that are dual substrates of CYP450 3A4 and P-glycoprotein. The potential changes cannot be predicted due to the conflicting effects of tipranavir and ritonavir on CYP450 3A4 and P-glycoprotein. Specifically, ritonavir is a potent inhibitor of CYP450 3A4 and tend to reduce clearance of substrate drugs, while tipranavir is a potent inducer of P-glycoprotein intestinal efflux transporters and tend to increase clearance of substrate drugs.', 'DDInter', 'Caution is advised if immunosuppressants such as cyclosporine, sirolimus, and tacrolimus are coadministered with tipranavir/ritonavir. More frequent concentration monitoring of these drugs is recommended until blood levels have been stabilized.', 'Metabolism', 'Caution is advised if immunosuppressants such as cyclosporine, sirolimus, and tacrolimus are coadministered with tipranavir/ritonavir.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/168317/', '', 'Minoxidil, Deoxycholic acid, Calcium gluconate, Magnesium sulfate, Glycopyrronium, Pimecrolimus, Aminobenzoic acid, Finasteride, Ivermectin, Caffeine, Diclofenac, More', 'Cabotegravir, Maraviroc, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, Brincidofovir, Elbasvir, Voxilaprevir, Pibrentasvir, Rimantadine, More', 1767369485);
