INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (168378, 'Ustekinumab', 'Talazoparib', 'Moderate', 'The use of ustekinumab with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', 'DDInter', 'Caution is advised if ustekinumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy.', 'Others', 'Caution is advised if ustekinumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/168554/', '', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Methotrexate, Abatacept, Inebilizumab, Belumosudil, Thalidomide, More', 'Pertuzumab, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Pegaspargase, Sotorasib, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, Anagrelide, More', 1767369485);
