INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (169487, 'Flibanserin', 'Temazepam', 'Moderate', 'The central nervous system (CNS) effects of flibanserin such as somnolence and fatigue may be potentiated by concomitant use of other agents with CNS depressant effects. The risk of CNS depression is increased if flibanserin is taken during waking hours or with alcohol or other CNS depressants.', 'DDInter', 'Patients receiving flibanserin with other agents that can cause CNS depression should be apprised of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness, and fatigue. Patients should not drive, operate machinery, or engage in hazardous activities requiring mental alertness and motor coordination for at least 6 hours after taking flibanserin.', 'Synergism', 'Patients receiving flibanserin with other agents that can cause CNS depression should be apprised of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness, and fatigue.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/169663/', '', 'Melatonin, Nitrazepam, Zopiclone, Methohexital', 'Ritodrine, Bremelanotide, Black cohosh', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (169488, 'Flumazenil', 'Temazepam', 'Moderate', 'The administration of flumazenil can result in convulsions in patients who have been receiving long-term treatment with benzodiazepines for epilepsy or sedation or who have recently received repeated doses of parenteral benzodiazepines. The mechanism is abrupt reversal of the benzodiazepine''s anticonvulsive effects. In addition, withdrawal seizures may occur in patients who are physically dependent on benzodiazepines.', 'DDInter', 'The use of flumazenil is not recommended in patients with epilepsy who have been receiving long-term treatment with benzodiazepines. In addition, flumazenil is contraindicated in patients who have received a benzodiazepine to control potentially life-threatening conditions such as status epilepticus or increased intracranial pressure. Extreme caution, dose individualization, and close monitoring is recommended for other patients. Clinicians should be prepared to manage convulsions. Most seizures have been successfully managed with benzodiazepines, phenytoin, or barbiturates.', 'Antagonism', 'The use of flumazenil is not recommended in patients with epilepsy who have been receiving long-term treatment with benzodiazepines.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/169664/', '', 'Suvorexant, Methohexital, Methylphenobarbital, Pentobarbital, Zaleplon, Dichloralphenazone, Valerian, Butabarbital, Secobarbital, Chloral hydrate, Paraldehyde, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Cobicistat, Deferoxamine, Levoleucovorin, Arginine, More', 1767369485);
