INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (169631, 'Teriflunomide', 'Temozolomide', 'Major', 'The use of leflunomide with other immunosuppressive or myelosuppressive agents may increase the risk of infections. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide. The recent, concomitant, or sequential use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', 'DDInter', 'Close monitoring is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immunosuppressive and hepatotoxic agents, and vice versa. Due to the prolonged elimination half-life of leflunomide''s active metabolite, an interaction may occur even when these agents are initiated after the discontinuation of treatment with leflunomide or teriflunomide. Liver enzymes, bilirubin, platelet, white blood cell count, and hemoglobin or hematocrit should be evaluated at baseline and regularly during therapy.', 'Synergism', 'Close monitoring is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immunosuppressive and hepatotoxic agents, and vice versa.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/169807/', '', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Tacrolimus, Abatacept, Apremilast, Inebilizumab, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (169632, 'Thalidomide', 'Temozolomide', 'Major', 'Coadministration of thalidomide with glucocorticoids and/or antineoplastic agents in the treatment of malignancy may potentiate the risk of thromboembolism. The exact mechanism is unknown but likely multifactorial.', 'DDInter', 'Close monitoring for DVT or pulmonary embolism is recommended in patients who require thalidomide therapy in combination with glucocorticoids and/or cytotoxic agents. Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain, shortness of breath, and pain or swelling in the arms or legs. Prophylaxis with anticoagulants such as low-molecular weight heparins or warfarin may be appropriate, but the decision to take thromboprophylactic measures should be made after careful assessment of underlying risk factors. If a thromboembolic event occurs during therapy with thalidomide, treatment must be discontinued and standard anticoagulation therapy started. Once anticoagulation is stabilized and complications of the thromboembolic event under control, thalidomide may be restarted at the original dose if benefit is deemed to outweigh the risks.', 'Synergism', 'Close monitoring for DVT or pulmonary embolism is recommended in patients who require thalidomide therapy in combination with glucocorticoids and/or cytotoxic agents.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/169808/', '', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Tacrolimus, Abatacept, Apremilast, Inebilizumab, More', '', 1767369485);
