INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (170056, 'Vortioxetine', 'Terbinafine', 'Major', 'Coadministration with potent inhibitors of CYP450 2D6 may significantly increase the plasma concentrations of vortioxetine, which is primarily metabolized by the isoenzyme. High plasma levels of vortioxetine may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucination, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', 'DDInter', 'The dosage of vortioxetine should be reduced by one-half when used in combination with potent CYP450 2D6 inhibitors such as bupropion, cinacalcet, fluoxetine, paroxetine, quinidine, ritonavir, and terbinafine. Following discontinuation of the potent CYP450 2D6 inhibitor, vortioxetine dosage should be returned to the original level. Since fluoxetine and paroxetine are also selective serotonin reuptake inhibitors, additive serotonergic effects with vortioxetine should be considered, and concomitant use avoided if possible.', 'Metabolism', 'The dosage of vortioxetine should be reduced by one-half when used in combination with potent CYP450 2D6 inhibitors such as bupropion, cinacalcet, fluoxetine, paroxetine, quinidine, ritonavir, and terbinafine.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/170232/', '', 'Salicylic acid, Fluconazole, Selenium Sulfide, Thiabendazole, Miconazole, Nystatin, Ciclopirox, Ketoconazole, Econazole, Flucytosine, Tioconazole, More', 'Phenelzine, Tranylcypromine, Desvenlafaxine, Maprotiline, St. John''s Wort, Isocarboxazid, Oxitriptan, Viloxazine, Milnacipran, Tryptophan, Levomilnacipran, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (170057, 'Human adenovirus e serotype 4 strain cl-68578 antigen', 'Teriflunomide', 'Moderate', 'There are no data regarding the use of live, unattenuated Adenovirus Type 4 and Type 7 vaccine during treatment with immunosuppressive therapies, including alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. It is not known whether immunosuppressed patients may have an increased risk of adverse reactions or a decreased or suboptimal immunologic response to the vaccine.', 'DDInter', 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies. Caution is advised.', 'Others', 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/170233/', '', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', '', 1767369485);
