INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (170122, 'Fostemsavir', 'Teriflunomide', 'Moderate', 'Coadministration with fostemsavir may increase the plasma concentrations of drugs that are substrates of the transporters, organic anion transporting polypeptides (OATP) 1B1/1B3 and breast cancer resistance protein (BCRP). Temsavir, the active moiety of fostemsavir, is an inhibitor of OATP 1B1 and 1B3. Additionally, temsavir and two metabolites are inhibitors of BCRP.', 'DDInter', 'Caution is advised when fostemsavir is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fostemsavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when fostemsavir is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/170298/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 'Glecaprevir, Cabotegravir, Amprenavir, Zanamivir, Valaciclovir, Lenacapavir, Telaprevir, Velpatasvir, Dolutegravir, Oseltamivir, Cobicistat, More', 1767369485);
