INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (170130, 'Glasdegib', 'Teriflunomide', 'Moderate', 'Coadministration with glasdegib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) efflux transporters.', 'DDInter', 'Caution is advised if glasdegib is coadministered with drugs that are substrates of P-gp and/or BCRP, particularly those with a narrow therapeutic range. Clinical and laboratory monitoring should be considered whenever glasdegib is added to or withdrawn from therapy with these drugs, and dosages adjusted as necessary. Patients should be monitored for the development of adverse effects.', 'Absorption', 'Caution is advised if glasdegib is coadministered with drugs that are substrates of P-gp and/or BCRP, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/170306/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 'Pertuzumab, Vismodegib, Belzutifan, Sonidegib, Mitotane, Porfimer sodium, Estramustine, Aminolevulinic acid, Tazemetostat, Vorinostat, Arsenic trioxide, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (170131, 'Glyburide', 'Teriflunomide', 'Moderate', 'Coadministration with teriflunomide may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of the organic anion transporting polypeptide (OATP) 1B1 and 1B3 and/or breast cancer resistance protein (BCRP) transporters. The proposed mechanism, based on in vivo data, is decreased clearance due to teriflunomide-mediated inhibition of OATP 1B1/1B3 or BCRP transport proteins.', 'DDInter', 'Caution is advised if teriflunomide is used concomitantly with drugs that are substrates of OATP 1B1/1B3 and/or BCRP. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever teriflunomide is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Excretion', 'Caution is advised if teriflunomide is used concomitantly with drugs that are substrates of OATP 1B1/1B3 and/or BCRP.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/170307/', '', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Tolazamide, Dulaglutide, Exenatide, Tolbutamide, Miglitol, Glimepiride, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (170132, 'Teriflunomide', 'Sodium aurothiomalate', 'Major', 'Coadministration of leflunomide with other immuno- or myelosuppressive antirheumatic agents may potentiate the risk of infections. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide. Serious infections including sepsis, as well as opportunistic infections like Pneumocystis jiroveci pneumonia, pulmonary and extrapulmonary tuberculosis, and aspergillosis have been reported with the use of leflunomide, particularly in patients on concomitant hematotoxic therapy.', 'DDInter', 'Concomitant use of leflunomide with other immuno- or myelosuppressive antirheumatic agents should generally be avoided. When switching from leflunomide to one of these agents, consideration should be given to administering a washout procedure with cholestyramine or activated charcoal to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years. This will decrease the overlap of systemic exposure to both compounds and reduce the likelihood of additive hematologic toxicities. However, the washout procedure may also induce disease worsening if the patient had been responding to leflunomide treatment. Patients who do not receive the washout procedure prior to switching should be monitored closely for hematologic toxicity. All patients treated with leflunomide or teriflunomide should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination. If evidence of serious infection or bone marrow suppression occurs, treatment should be stopped and washout procedure administered.', 'Others', 'Concomitant use of leflunomide with other immuno- or myelosuppressive antirheumatic agents should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/170308/', '', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Anifrolumab, Secukinumab, Canakinumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, Tacrolimus, Abatacept, More', '', 1767369485);
