INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (170178, 'Benzylpenicillin (sodium)', 'Teriflunomide', 'Moderate', 'Coadministration with teriflunomide may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of organic anion transporter 3 (OAT3). The proposed mechanism, based on in vivo data, is decreased clearance due to teriflunomide-mediated inhibition of OAT3 transporter.', 'DDInter', 'Caution is advised if teriflunomide is used concomitantly with drugs that are substrates of OAT3. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever teriflunomide is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Excretion', 'Caution is advised if teriflunomide is used concomitantly with drugs that are substrates of OAT3.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/170354/', '', 'Sulbactam, Oxacillin, Dicloxacillin, Piperacillin, Meticillin, Carbenicillin, Bacampicillin, Ticarcillin, Mezlocillin, Tazobactam, Cloxacillin, More', 'Golimumab, Cyclosporine, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Tacrolimus, More', 1767369485);
