INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (170677, 'Zoledronic acid', 'Thalidomide', 'Moderate', 'In multiple myeloma patients, the risk of renal dysfunction may be increased when intravenous bisphosphonates are used in combination with thalidomide. The mechanism of interaction has not been described.', 'DDInter', 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in multiple myeloma patients who are receiving treatment with thalidomide. Renal function and serum electrolytes should be monitored. Product labelings for pamidronate and zoledronic acid recommend that single doses not exceed 90 mg and 4 mg, respectively, and the duration of infusion not less than 2 hours and 15 minutes, respectively. Patients should have serum creatinine assessed prior to each treatment, and treatment should be withheld for renal deterioration. In those treated for bone metastases, treatment should not be resumed until renal function returns to baseline.', 'Synergism', 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in multiple myeloma patients who are receiving treatment with thalidomide.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/170863/', '', 'Tiludronic acid, Cholecalciferol, Vosoritide, Burosumab', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 1767369485);
