INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (171423, 'Timolol', 'Atracurium', 'Moderate', 'Neuromuscular blocking agents and beta-blockers may have additive cardiovascular depressant effects. Limited data suggest that beta-blockers may modestly potentiate the pharmacologic effects of neuromuscular blocking agents. The exact mechanism of interaction is unknown but may involve effects in the postsynaptic membrane, as beta-blockers alone have been reported to exacerbate or unmask myasthenia gravis.', 'DDInter', 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers. Respiratory and cardiovascular status should be closely monitored.', 'Synergism', 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171610/', '', 'Latanoprost, Apraclonidine, Neostigmine, Pilocarpine, Clonidine, Physostigmine, Acetylcholine, Bimatoprost, Travoprost, Brinzolamide, Epinephrine, More', 'Botulinum toxin type A', 1767369485);
