INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (171426, 'Timolol', 'Bendroflumethiazide', 'Moderate', 'Although they are often combined in clinical practice, diuretics and beta-blockers may increase the risk of hyperglycemia and hypertriglyceridemia in some patients, especially in patients with diabetes or latent diabetes. In addition, the risk of QT interval prolongation and arrhythmias (e.g. torsades de pointes) due to sotalol may be increased by potassium-depleting diuretics.', 'DDInter', 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration. Patients should be advised to seek medical assistance if they experience dizziness, weakness, fainting, fast or irregular heartbeats, or loss of blood glucose control.', 'Synergism', 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171613/', '', 'Esmolol, Carteolol, Penbutolol, Latanoprost, Methazolamide, Neostigmine, Pilocarpine, Clonidine, Physostigmine, Acetylcholine, Diclofenamide, More', 'Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Ciclopirox, Darunavir, Ketoconazole, More', 1767369485);
