INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (171689, 'Felbamate', 'Tipranavir', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171877/', '', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Cobicistat, Acyclovir, More', 'Brivaracetam, Ezogabine, Lamotrigine, Ethosuximide, Lacosamide, Tiagabine, Ethotoin, Rufinamide, Paramethadione, Perampanel, Ganaxolone, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (171690, 'Felodipine', 'Tipranavir', 'Moderate', 'Coadministration with tipranavir in combination with ritonavir may alter the pharmacokinetics of calcium channel blockers that are dual substrates of CYP450 3A4 and P-glycoprotein. The potential changes cannot be predicted due to the conflicting effects of tipranavir and ritonavir on CYP450 3A4 and P-glycoprotein. Specifically, ritonavir is a potent inhibitor of CYP450 3A4 and tend to reduce clearance of substrate drugs, while tipranavir is a potent inducer of P-glycoprotein intestinal efflux transporters and tend to increase clearance of substrate drugs.', 'DDInter', 'Caution is advised if calcium channel blockers such as diltiazem, felodipine, nicardipine, nisoldipine, and verapamil are coadministered with tipranavir/ritonavir. Patients should be monitored for potentially altered pharmacologic response to calcium channel blocker therapy following initiation or discontinuation of tipranavir/ritonavir.', 'Metabolism, Excretion', 'Caution is advised if calcium channel blockers such as diltiazem, felodipine, nicardipine, nisoldipine, and verapamil are coadministered with tipranavir/ritonavir.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171878/', '', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, Brincidofovir, Stavudine, More', 'Amlodipine, Metoprolol, Carvedilol, Nebivolol, Nifedipine, Bisoprolol, Ivabradine, Sotalol, Atenolol, Amlodipine, Isradipine, More', 1767369485);
