INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (172034, 'Gentamicin', 'Tobramycin', 'Moderate', 'Use of more than one aminoglycoside concurrently or sequentially may increase the risk of nephrotoxicity and ototoxicity. The risk is greatest in patients with impaired renal function, those receiving high doses for prolonged periods of time, elderly patients, and dehydrated patients. Ototoxicity may occur without renal toxicity. Oral neomycin undergoes systemic absorption and has also been associated with nephrotoxicity and ototoxicity.', 'DDInter', 'The use of more than one aminoglycoside should be avoided. Patients receiving aminoglycosides should be closely monitored for development of toxicity, including renal function tests and serial, vestibular, and audiometric tests. Ototoxicity may be permanent and may not be apparent until long after therapy has been discontinued. The onset of auditory toxicity may be asymptomatic or may manifest with early symptoms such as feelings of fullness in the ear, high tone hearing loss, or tinnitus. Signs of vestibulotoxicity include ataxia, tinnitus, and vertigo.', 'Synergism', 'The use of more than one aminoglycoside should be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/172222/', '', 'Tetracycline, Gatifloxacin, Ganciclovir, Rifamycin, Oxytetracycline, Norfloxacin, Lomefloxacin, Sulfamethizole, Trifluridine, Moxifloxacin, Sulfisoxazole, More', 'Tetracycline, Ganciclovir, Norfloxacin, Rifamycin, Oxytetracycline, Lomefloxacin, Famciclovir, Trifluridine, Sulfamethizole, Tobramycin, Benzylpenicillin, More', 1767369485);
