INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (172201, 'Fosaprepitant', 'Tofacitinib', 'Moderate', 'Coadministration with one or more medications that may result in both moderate inhibition of CYP450 3A4 and potent inhibition of CYP450 2C19 may significantly increase the plasma concentrations of tofacitinib, which is primarily metabolized by the former isoenzyme with minor contribution from the latter. Side effects including lymphopenia, neutropenia, anemia, serious infections, and hyperlipidemia may be increased.', 'DDInter', 'The dosage of tofacitinib should be reduced by 50% when used concomitantly with one or more medications that may result in both moderate inhibition of CYP450 3A4 and potent inhibition of CYP450 2C19. For example, the dose for patients receiving 10 mg twice daily should be reduced to 5 mg twice daily and the dose for patients receiving 5 mg twice daily should be reduced to 5 mg once daily. For patients receiving 11 mg once daily of the extended-release formulation, the dose should be reduced to 5 mg once daily of the immediate-release formulation. The dose for patients receiving 3.2 mg twice daily should be reduced to 3.2 mg once daily and the dose for patients receiving 4 mg twice daily should be reduced to 4 mg once daily. Moderate inhibitors of CYP450 3A4 include amiodarone, aprepitant, ciprofloxacin, crizotinib, darunavir, dalfopristin-quinupristin, diltiazem, dronedarone, erythromycin, fluconazole, fusidic acid, grapefruit juice, imatinib, isavuconazonium, netupitant, and verapamil. Potent inhibitors of CYP450 2C19 include fluconazole, fluvoxamine, esomeprazole, lansoprazole, and omeprazole. Inhibitors of CYP450 2C19 alone are unlikely to substantially alter the pharmacokinetics of tofacitinib.', 'Metabolism', 'The dosage of tofacitinib should be reduced by 50% when used concomitantly with one or more medications that may result in both moderate inhibition of CYP450 3A4 and potent inhibition of CYP450 2C19.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/172389/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (172202, 'Fosphenytoin', 'Tofacitinib', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of tofacitinib, which is primarily metabolized by the isoenzyme. The interaction may result in loss of or reduced clinical response.', 'DDInter', 'The use of tofacitinib in combination with potent CYP450 3A4 inducers should generally be avoided. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. The extent to which moderate CYP450 3A4 inducers interact with tofacitinib is unknown.', 'Metabolism', 'The use of tofacitinib in combination with potent CYP450 3A4 inducers should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/172390/', '', 'Brivaracetam, Topiramate, Ezogabine, Lamotrigine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Ethotoin, Clonazepam, Paramethadione, More', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Abatacept, Inebilizumab, Cladribine, Ozanimod, Belimumab, Sutimlimab, Risankizumab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (172203, 'Fostamatinib', 'Tofacitinib', 'Moderate', 'Coadministration with fostamatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine and/or liver due to inhibition of CYP450 3A4-mediated metabolism and P-gp-mediated efflux by fostamatinib.', 'DDInter', 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fostamatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/172391/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, Siponimod, More', 'Eltrombopag, Romiplostim, Avatrombopag, Emicizumab, Coagulation factor VIIa Recombinant Human, Coagulation factor X human, Phylloquinone, Lusutrombopag, Anti-inhibitor coagulant complex, Antihemophilic factor, human recombinant, Fibrinogen human, More', 1767369485);
