INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (172696, 'Abarelix', 'Toremifene', 'Major', 'Toremifene has the potential to prolong the QT interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of toremifene with other drugs that can prolong the QT interval should generally be avoided. If treatment with other QT-prolonging drugs is required, interruption of toremifene therapy should be considered. Caution and clinical monitoring are recommended if concomitant use is unavoidable. In patients at increased risk, electrocardiograms (ECGs) are recommended at baseline and as clinically indicated. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of toremifene with other drugs that can prolong the QT interval should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/172884/', '', 'Darolutamide, Anastrozole, Fulvestrant, Toremifene, Letrozole, Exemestane', 'Darolutamide, Anastrozole, Aminoglutethimide, Letrozole, Fulvestrant, Abarelix, Flutamide, Enzalutamide, Relugolix, Bicalutamide, Degarelix, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (172697, 'Aminoglutethimide', 'Toremifene', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of toremifene. According to the product labeling, toremifene is primarily metabolized by CYP450 3A4 to N-demethyltoremifene, an antiestrogenic metabolite with weak in vivo antitumor potency but whose serum concentrations are 2 to 4 times higher than those of toremifene at steady state. The extent to which other, less potent CYP450 3A4 inducers may affect toremifene is unknown.', 'DDInter', 'The potential for diminished pharmacologic effects of toremifene should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments or a dose adjustment of toremifene may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of toremifene should be considered during coadministration with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/172885/', '', 'Darolutamide, Anastrozole, Fulvestrant, Toremifene, Letrozole, Exemestane', 'Anastrozole, Aminoglutethimide, Letrozole, Fulvestrant, Abarelix, Flutamide, Bicalutamide, Degarelix, Nilutamide, Apalutamide, Abiraterone', 1767369485);
