INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (172889, 'Iloperidone', 'Torasemide', 'Moderate', 'Some neuroleptic agents may cause prolongation of the QT interval. While clinical data are lacking, the coadministration of other agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes. In addition, neuroleptic agents may potentiate the hypotensive effect of diuretics secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during the initial dose titration period of neuroleptic therapy.', 'DDInter', 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be monitored and any abnormalities corrected prior to initiating therapy with a neuroleptic. Close clinical monitoring for development of hypotension is recommended if neuroleptic agents are prescribed with a diuretic medication. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. A lower starting dosage and slower titration of the neuroleptic agent may be appropriate in patients receiving antihypertensive therapy, especially if they are elderly.', 'Synergism', 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173077/', '', 'Tinidazole, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Clindamycin, Ciclopirox, Probenecid, More', 'Pimavanserin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (172890, 'Iloprost', 'Torasemide', 'Moderate', 'Coadministration of medicines targeting the prostacyclin pathway (e.g., prostacyclin (PGI2), PGI2 analogs, or selective non-prostanoid prostacyclin IP receptor agonists) with diuretics, antihypertensive agents, or other vasodilators may potentiate the hypotensive effects of these agents. The mechanism involves the additive reduction in blood pressure due to the vasodilatory effects of PGI2, PGI2 analogs or selective non-prostanoid prostacyclin IP receptor agonists on the prostacyclin pathway.', 'DDInter', 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently. If these drugs are used together, it is generally recommended that blood pressure be measured more frequently until a stable blood pressure pattern is observed. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostatic hypotension, or tachycardia.', 'Synergism', 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173078/', '', 'Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Amphotericin B, Clindamycin, Ciclopirox, Vemurafenib, More', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Lepirudin, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (172891, 'Imatinib', 'Torasemide', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of torsemide. The interaction has not been evaluated in pharmacokinetic studies; however, torsemide has been shown to undergo metabolism by CYP450 2C9.', 'DDInter', 'Caution is advised when torsemide is prescribed with CYP450 2C9 inhibitors. Patients should be monitored for potentially increased adverse effects such as dehydration, hypotension, electrolyte imbalances, hyperglycemia, tinnitus, and hearing loss.', 'Metabolism', 'Caution is advised when torsemide is prescribed with CYP450 2C9 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173079/', '', 'Etacrynic acid, Bumetanide, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Sulfadiazine, Lactic acid, Methazolamide, Clindamycin, More', 'Entrectinib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, Lapatinib, More', 1767369485);
