INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (173143, 'Eplerenone', 'Trandolapril', 'Moderate', 'Coadministration of eplerenone with ACE inhibitors or angiotensin II receptor antagonists may increase the risk of hyperkalemia due to additive pharmacodynamic effects.', 'DDInter', 'Given the potential for serious, sometimes fatal cardiac arrhythmias associated with hyperkalemia, caution is advised if eplerenone is administered with ACE inhibitors or angiotensin II receptor antagonists. Periodic monitoring of serum potassium levels is recommended until the effect of eplerenone is established. In clinical trials, monitoring occurred every 2 weeks for the first 1 to 2 months, then monthly thereafter. Particular caution is warranted in patients with renal impairment, diabetes, old age, or dehydration. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', 'Given the potential for serious, sometimes fatal cardiac arrhythmias associated with hyperkalemia, caution is advised if eplerenone is administered with ACE inhibitors or angiotensin II receptor antagonists.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173331/', '', 'Felodipine, Indapamide', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (173144, 'Epoprostenol', 'Trandolapril', 'Moderate', 'Coadministration of medicines targeting the prostacyclin pathway (e.g., prostacyclin (PGI2), PGI2 analogs, or selective non-prostanoid prostacyclin IP receptor agonists) with diuretics, antihypertensive agents, or other vasodilators may potentiate the hypotensive effects of these agents. The mechanism involves the additive reduction in blood pressure due to the vasodilatory effects of PGI2, PGI2 analogs or selective non-prostanoid prostacyclin IP receptor agonists on the prostacyclin pathway.', 'DDInter', 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently. If these drugs are used together, it is generally recommended that blood pressure be measured more frequently until a stable blood pressure pattern is observed. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostatic hypotension, or tachycardia.', 'Synergism', 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173332/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Lepirudin, Acenocoumarol, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, More', '', 1767369485);
