INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (173293, 'Tranylcypromine', 'Mephentermine', 'Major', 'Indirect- or mixed-acting sympathomimetic amines may precipitate severe hypertensive reactions and hyperpyrexia in patients treated with monoamine oxidase inhibitors (MAOIs). Death has occurred in some reported cases. The mechanism involves a synergistic sympathomimetic effect due to enhanced norepinephrine storage in adrenergic neurons (MAOI activity) and increased liberation of catecholamines (indirect sympathomimetic activity).', 'DDInter', 'In general, indirect- and mixed-acting sympathomimetic agents should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, methylene blue, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with sympathomimetic agents.', 'Synergism', 'In general, indirect- and mixed-acting sympathomimetic agents should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173481/', '', 'Citalopram, Sertraline, Fluoxetine, St. John''s Wort, Vortioxetine, Doxepin, Viloxazine, Amoxapine, Imipramine, Esketamine, Fluvoxamine, More', 'Arbutamine, Angiotensin II', 1767369485);
