INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (173473, 'Demeclocycline', 'Tretinoin', 'Major', 'Concomitant use of vitamin A or related compounds (i.e., retinoids) with tetracyclines may increase the risk of pseudotumor cerebri, also known as benign intracranial hypertension. These agents individually have been associated with the development of pseudotumor cerebri and may have additive effects during coadministration. The interaction has been suspected in reported cases involving the use of isotretinoin or vitamin A in combination with a tetracycline.', 'DDInter', 'Coadministration of most retinoids with tetracyclines is considered contraindicated. Patients treated with a retinoid or a tetracycline should be advised to discontinue the medication and notify their physician immediately if they develop early symptoms of pseudotumor cerebri such as headache, nausea, vomiting, visual disturbances, vision loss, and papilledema. Although intracranial hypertension typically resolves after discontinuation of treatment, the possibility for permanent visual loss exists. Therefore, prompt ophthalmologic evaluation should be performed if visual disturbances occur. Since intracranial pressure may remain elevated for weeks after drug cessation, patients should be monitored until they stabilize.', 'Synergism', 'Coadministration of most retinoids with tetracyclines is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173661/', '', 'Tigecycline, Mupirocin, Rifamycin, Bacitracin, Chloramphenicol, Rifaximin, Gentamicin, Amikacin, Neomycin', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Ixazomib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (173474, 'Echinacea', 'Tretinoin', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', 'DDInter', 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173662/', '', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Sotorasib, Enasidenib, More', '', 1767369485);
