INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (173495, 'Tretinoin', 'Oxytetracycline', 'Major', 'Concomitant use of vitamin A or related compounds (i.e., retinoids) with tetracyclines may increase the risk of pseudotumor cerebri, also known as benign intracranial hypertension. These agents individually have been associated with the development of pseudotumor cerebri and may have additive effects during coadministration. The interaction has been suspected in reported cases involving the use of isotretinoin or vitamin A in combination with a tetracycline.', 'DDInter', 'Coadministration of most retinoids with tetracyclines is considered contraindicated. Patients treated with a retinoid or a tetracycline should be advised to discontinue the medication and notify their physician immediately if they develop early symptoms of pseudotumor cerebri such as headache, nausea, vomiting, visual disturbances, vision loss, and papilledema. Although intracranial hypertension typically resolves after discontinuation of treatment, the possibility for permanent visual loss exists. Therefore, prompt ophthalmologic evaluation should be performed if visual disturbances occur. Since intracranial pressure may remain elevated for weeks after drug cessation, patients should be monitored until they stabilize.', 'Synergism', 'Coadministration of most retinoids with tetracyclines is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173683/', '', 'Tigecycline, Ganciclovir, Rifamycin, Kanamycin, Netilmicin, Polymyxin B, Sulfamethizole, Trifluridine, Benzylpenicillin, Chloramphenicol, Sulfisoxazole, More', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Ixazomib, More', 1767369485);
