INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (173505, 'Vemurafenib', 'Tretinoin', 'Moderate', 'Coadministration with vemurafenib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 2C8. The proposed mechanism, based on in vitro data, is decreased clearance due to vemurafenib-mediated inhibition of CYP450 2C8.', 'DDInter', 'Caution is advised if vemurafenib is used concomitantly with drugs that are substrates of CYP450 2C8, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever vemurafenib is added to or withdrawn from therapy with these drugs. Since vemurafenib has a long half-life, a wash-out period of 8 days following the discontinuation of vemurafenib has been suggested to avoid an interaction with subsequent therapy. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised if vemurafenib is used concomitantly with drugs that are substrates of CYP450 2C8, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173693/', '', 'Pertuzumab, Tisagenlecleucel, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Selinexor, Aflibercept, Trastuzumab, Hydroxyurea, Talimogene laherparepvec, More', 'Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Trichlormethiazide, Probenecid, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (173506, 'Verteporfin', 'Tretinoin', 'Moderate', 'Concomitant use of verteporfin with other known photosensitizing agents may increase the risk of photosensitivity reactions. Medicinal products with known phototoxic or photoallergic potential include fluoroquinolones, phenothiazines, retinoids, sulfonamides, sulfonylureas, tetracyclines, thiazide diuretics, griseofulvin, and hypericin extracts (e.g., St John''s Wort).', 'DDInter', 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided. Patients should be advised to avoid exposure of skin or eyes to sunlight or bright indoor lights (e.g., examination lamps, operating room lamps, tanning beds, lights at close proximity) for 5 days after administration of verteporfin.', 'Synergism', 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173694/', '', 'Aflibercept, Bevacizumab', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, More', 1767369485);
