INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (173762, 'Fluvoxamine', 'Triazolam', 'Moderate', 'Administration of fluvoxamine with certain benzodiazepines may increase CNS depression (e.g., sedation, ataxia). The mechanism appears to be inhibition of the CYP450 3A4 hepatic oxidative metabolism of the benzodiazepine.', 'DDInter', 'The patient should be observed for increased CNS depression. Dosage adjustments may be required. Lorazepam, temazepam, and oxazepam, which are not metabolized by oxidative metabolism, may be better alternatives if a benzodiazepine is required.', 'Metabolism', 'The patient should be observed for increased CNS depression.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173950/', '', 'Levomilnacipran, Oxitriptan, Vortioxetine, Milnacipran, Tryptophan, Vilazodone', 'Thiopental, Ethchlorvynol, Zopiclone, Dichloralphenazone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (173763, 'Fosamprenavir', 'Triazolam', 'Major', 'Coadministration with protease inhibitors (PIs) may significantly increase the plasma concentrations and pharmacologic effects of orally administered midazolam and triazolam. The mechanism is PI inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of these benzodiazepines. Within the class, ritonavir is the most potent CYP450 3A4 inhibitor, while saquinavir is considered one of the weaker ones.', 'DDInter', 'Given the potential for prolonged and/or increased sedation and respiratory depression associated with excessive benzodiazepine blood levels, concomitant use of oral midazolam or triazolam with protease inhibitors is considered contraindicated. Caution and close clinical monitoring are recommended when administering parenteral midazolam in combination with protease inhibitors. Appropriate medical management should be readily available in case of respiratory depression and/or prolonged sedation. Dosage adjustment for midazolam may be appropriate, especially if more than a single dose of midazolam is administered.', 'Metabolism', 'Given the potential for prolonged and/or increased sedation and respiratory depression associated with excessive benzodiazepine blood levels, concomitant use of oral midazolam or triazolam with protease inhibitors is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173951/', '', 'Temazepam, Remimazolam, Chloral hydrate, Nitrazepam, Thiopental, Paraldehyde, Propiomazine, Meprobamate, Scopolamine, Dexmedetomidine, Methohexital, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Brincidofovir, Remdesivir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (173764, 'Fosaprepitant', 'Triazolam', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of benzodiazepines that are metabolized by CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant.', 'DDInter', 'Caution is advised if aprepitant or fosaprepitant is administered with benzodiazepines that are metabolized by CYP450 3A4. The potential for increased pharmacologic effects of the benzodiazepine, including central nervous system and respiratory depression, should be considered. The interaction is not expected to occur with lorazepam, oxazepam or temazepam, which are primarily metabolized via glucuronidation. Chronic, continuous use of aprepitant for prevention of nausea and vomiting is not recommended because it has not been studied and because the drug interaction profile may change during long-term use.', 'Metabolism', 'Caution is advised if aprepitant or fosaprepitant is administered with benzodiazepines that are metabolized by CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/173952/', '', 'Flurazepam, Methohexital, Zaleplon, Dichloralphenazone, Valerian, Temazepam, Chloral hydrate, Paraldehyde, Ramelteon, Ethchlorvynol, Zopiclone, More', '', 1767369485);
