INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (173996, 'Esterified estrogens', 'Trimipramine', 'Minor', 'The effects of tricyclic antidepressants (TCAs) may be altered in women receiving estrogen-containing therapy. Simultaneous TCA toxicity and reduced effects have been reported. Akathisia has also been reported in some women taking this combination. The mechanism of interaction is unknown but may be related to increased TCA bioavailability or inhibition of hepatic TCA metabolism.', 'DDInter', 'The clinical significance of this interaction has not been established. Monitoring for altered effects may be advisable during concomitant therapy. Dose adjustments of the TCA may be required if an interaction is suspected.', 'Others', 'The clinical significance of this interaction has not been established.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/174184/', '', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, Milnacipran, Vortioxetine, Levomilnacipran, Duloxetine, Isocarboxazid, Viloxazine, Esketamine, More', '', 1767369485);
