INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (174101, 'Trimipramine', 'Physostigmine (ophthalmic)', 'Moderate', 'Theoretically, anticholinergic agents and other agents with significant anticholinergic activity (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, class IA antiarrhythmics especially disopyramide) may antagonize the effects of topically administered cholinergic agents such as acetylcholine, carbachol, demecarium, echothiophate, isoflurophate, physostigmine, and pilocarpine. The proposed mechanism involves opposing pharmacodynamic action on muscarinic receptor sites in ocular tissue. This interaction is sometimes desirable and is the basis for using atropine in the treatment of excessive muscarinic side effects and cholinergic crisis induced by cholinergic overdose.', 'DDInter', 'Patients receiving long-term therapy with anticholinergic agents should be monitored for potentially diminished therapeutic (miotic) response to ophthalmic cholinergic therapy, and dosages adjusted as necessary.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/174289/', '', 'Citalopram, Sertraline, Fluoxetine, St. John''s Wort, Milnacipran, Vortioxetine, Levomilnacipran, Duloxetine, Isocarboxazid, Viloxazine, Esketamine, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (174102, 'Trimipramine', 'Polythiazide', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/174290/', '', 'Desipramine, St. John''s Wort, Oxitriptan, Viloxazine, Nortriptyline, Tryptophan, Protriptyline, Esketamine', 'Tinidazole, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Clindamycin, Ciclopirox, Darunavir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (174103, 'Trimipramine', 'Potassium chloride', 'Major', 'Concomitant use of agents with anticholinergic properties (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, the class IA antiarrhythmic disopyramide) may potentiate the risk of upper gastrointestinal injury associated with oral solid formulations of potassium chloride. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents, thereby creating a high localized concentration of potassium ions in the region of a dissolving tablet or capsule and increasing the contact time with GI mucosa. Solid formulations of potassium chloride have been associated with upper GI bleeding and small bowel ulceration, stenosis, perforation, and obstruction.', 'DDInter', 'The use of certain oral solid formulations of potassium chloride is considered contraindicated in patients receiving agents with anticholinergic properties at sufficient doses to exert anticholinergic effects. A liquid formulation of potassium chloride should be considered. Patients prescribed a solid oral formulation should be advised to discontinue potassium therapy and contact their physician if they experience potential symptoms of upper GI injury such as severe vomiting, abdominal pain, distention, and gastrointestinal bleeding.', 'Others', 'The use of certain oral solid formulations of potassium chloride is considered contraindicated in patients receiving agents with anticholinergic properties at sufficient doses to exert anticholinergic effects.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/174291/', '', 'Phenelzine, Tranylcypromine, Desvenlafaxine, St. John''s Wort, Isocarboxazid, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, More', 'Potassium bicarbonate, Potassium gluconate, Calcium chloride, Zinc sulfate, Magnesium sulfate, Sodium acetate, Arginine, Sodium glycerophosphate, Zinc chloride, Sodium bicarbonate, Sodium chloride, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (174104, 'Trimipramine', 'Primaquine', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/174292/', '', 'Phenelzine, Tranylcypromine, Vilazodone, Paroxetine, Desvenlafaxine, St. John''s Wort, Nefazodone, Isocarboxazid, Oxitriptan, Viloxazine, Vortioxetine, More', 'Artesunate, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Proguanil, Sulfadoxine', 1767369485);
