INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (175194, 'Vasopressin', 'Nitroglycerin', 'Minor', 'Nitroglycerin has been used safely and successfully to counteract some of the potentially deleterious pressor effects of vasopressin. The combination may be particularly useful if vasopressin is required in a patient with known or suspected coronary artery disease. Animal data have shown that neither drug significantly affects the plasma clearance of the other. While nitroglycerin and vasopressin have opposing hemodynamic effects, the interaction has been used to clinical advantage.', 'DDInter', '-', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/175403/', '', 'Isosorbide mononitrate, Nesiritide, Isosorbide dinitrate, Vericiguat, Lidocaine, Zinc sulfate, Diltiazem, Tetracaine, Zinc acetate, Fluocinolone acetonide, Hydrocortisone, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (175195, 'Vasopressin', 'Papaverine', 'Major', 'Intracoronary administration of papaverine has been associated with QT interval prolongation and torsade de pointes (TdP) arrhythmia. The risk may theoretically increase in patients receiving concomitant medications that can also prolong the QT interval or cause bradycardia. The precise mechanism of papaverine-induced ventricular tachyarrhythmias has not been delineated, but may involve inhibition of potassium currents and prolongation of the action potential duration.', 'DDInter', 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/175404/', '', 'Darifenacin, Tadalafil, Ammonium chloride, Yohimbine, Oxybutynin, Dimethyl sulfoxide, Pentosan polysulfate, Calcium chloride, Trospium, Flavoxate, Avanafil, More', '', 1767369485);
