INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (175381, 'Venetoclax', 'Artesunate', 'Moderate', 'Coadministration with venetoclax may increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp). The proposed mechanism is decreased clearance due to inhibition of P-glycoprotein-mediated drug efflux in the intestine, liver, and/or kidney by venetoclax.', 'DDInter', 'Caution is advised when venetoclax is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range such as digoxin and dabigatran etexilate. Alternatives should be considered whenever possible. The manufacturer recommends that drugs sensitive to P-gp inhibition in the gastrointestinal tract be administered at least 6 hours before venetoclax. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate following the initiation or discontinuation of venetoclax.', 'Others', 'Caution is advised when venetoclax is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range such as digoxin and dabigatran etexilate.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/175590/', '', 'Mefloquine, Lumefantrine, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Hydroxychloroquine, Halofantrine, Primaquine, Proguanil, Sulfadoxine, Chloroquine', 'Pertuzumab, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (175382, 'Venetoclax', 'Bacillus calmette-guerin substrain tice live antigen', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', 'DDInter', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/175591/', '', 'Pertuzumab, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Sotorasib, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, Anagrelide, Ivosidenib, More', '', 1767369485);
