INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (175435, 'Isoniazid', 'Venetoclax', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of venetoclax, which is a substrate of both the isoenzyme and efflux transporter. Increased venetoclax exposure may potentiate the risk of tumor lysis syndrome, particularly at initiation of therapy and during the dosage ramp-up phase, as well as other adverse effects such as diarrhea, nausea, vomiting, neutropenia, anemia, and thrombocytopenia.', 'DDInter', 'Caution is advised when venetoclax is used with inhibitors of CYP450 3A4. Close clinical and laboratory monitoring for the development of tumor lysis syndrome and myelosuppression is recommended following the addition of a CYP450 3A4, and the venetoclax dosage adjusted if necessary. The dosage may be readjusted 2 to 3 days after discontinuation of the inhibitor.', 'Metabolism', 'Caution is advised when venetoclax is used with inhibitors of CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/175644/', '', 'Capreomycin, Pyrazinamide, Rifamycin, Bedaquiline, Sulfamethoxazole, Streptomycin, Trimethoprim, Cycloserine, Ethambutol, Pyridoxine, Ethionamide, More', 'Pertuzumab, Rucaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Sotorasib, Enasidenib, Mitotane, Niraparib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (175436, 'Venetoclax', 'Ketoconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of venetoclax, which is primarily metabolized by the isoenzyme. Additionally, inhibition of P-glycoprotein (P-gp) and breast cancer resistance protein (BCRP) efflux transporters by ketoconazole may contribute. Increased venetoclax exposure may potentiate the risk of tumor lysis syndrome, particularly at initiation of therapy and during the dosage ramp-up phase, as well as other adverse effects such as diarrhea, nausea, vomiting, neutropenia, anemia, and thrombocytopenia.', 'DDInter', 'Concomitant use of potent CYP450 3A4 inhibitors at initiation and during the dosage ramp-up phase of venetoclax is considered contraindicated. If a potent CYP450 3A4 inhibitor is required after the ramp-up phase, the manufacturer recommends that venetoclax dosage be reduced by at least 75%. The dosage used prior to initiating the CYP450 3A4 inhibitor may be resumed 2 to 3 days after discontinuation of the inhibitor.', 'Metabolism', 'Concomitant use of potent CYP450 3A4 inhibitors at initiation and during the dosage ramp-up phase of venetoclax is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/175645/', '', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, More', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Mitotane, Selinexor, Aflibercept, More', 1767369485);
