INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (175654, 'Venlafaxine', 'Telaprevir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of venlafaxine and its metabolite, O-desmethylvenlafaxine (ODV). The possibility of prolonged and/or increased pharmacologic effects of venlafaxine should be considered. Although venlafaxine is primarily metabolized by CYP450 2D6, there is some evidence that CYP450 3A4 may play a minor role, thus any alteration in its activity levels could conceivably affect the metabolism of venlafaxine. The interaction may be of greater concern in patients who are CYP450 2D6-deficient, or so-called poor metabolizers of CYP450 2D6 (approximately 7% of Caucasians and less than 2% of Asians and individuals of African descent).', 'DDInter', 'Caution is recommended when venlafaxine is used with CYP450 3A4 inhibitors.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/175863/', '', 'Sertraline, Maprotiline, Fluoxetine, Milnacipran, Vortioxetine, Doxepin, Duloxetine, Isocarboxazid, Viloxazine, Amoxapine, Imipramine, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Etravirine, Dolutegravir, Oseltamivir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (175655, 'Venlafaxine', 'Terbutaline', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. The use of SNRIs alone has been associated with sustained increases in blood pressure and heart rate, and cases of elevated blood pressure requiring immediate treatment have been reported in postmarketing experience.', 'DDInter', 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter. Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/175864/', '', 'Paroxetine, St. John''s Wort, Nefazodone, Oxitriptan, Viloxazine, Vortioxetine, Tryptophan, Vilazodone, Esketamine, Fluvoxamine', 'Mometasone furoate, Umeclidinium, Aclidinium, Glycopyrronium, Mometasone, Cromoglicic acid, Ipratropium, Beclomethasone dipropionate', 1767369485);
