INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (175819, 'Enzalutamide', 'Vilazodone', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of vilazodone, which is primarily metabolized by the isoenzyme.', 'DDInter', 'The possibility of diminished therapeutic response to vilazodone should be considered during coadministration with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, phenobarbital, phenytoin, and rifampin. When concomitant use is prescribed for greater than 14 days, an increase in the vilazodone dosage up to 2-fold may be considered depending on clinical response, up to a maximum of 80 mg daily. Following discontinuation of the CYP450 3A4 inducer, the dosage of vilazodone should be returned to the original level in 14 days.', 'Metabolism', 'The possibility of diminished therapeutic response to vilazodone should be considered during coadministration with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, phenobarbital, phenytoin, and rifampin.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/176029/', '', 'Phenelzine, Duloxetine, Tranylcypromine, Desvenlafaxine, Isocarboxazid, Oxitriptan, Viloxazine, Milnacipran, Tryptophan, Levomilnacipran, Esketamine, Paroxetine', 'Darolutamide, Tamoxifen, Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Relugolix, Bicalutamide, Degarelix, More', 1767369485);
