INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (176197, 'Glipizide', 'Voriconazole', 'Major', 'Coadministration with inhibitors of CYP450 2C9 including certain azole antifungal agents such as fluconazole, miconazole, and voriconazole may increase the plasma concentrations of sulfonylureas, many of which have been found to be substrates of the isoenzyme.', 'DDInter', 'Caution is advised when sulfonylureas are used concomitantly with azole antifungal agents that inhibit CYP450 2C9. Blood glucose should be closely monitored, and the sulfonylurea dosage adjusted as necessary. Patients should also be apprised of the increased risk of hypoglycemia and be alert to potential signs and symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypoglycemia occurs, patients should initiate appropriate remedial therapy immediately, discontinue the azole antifungal agent, and contact their physician.', 'Metabolism', 'Caution is advised when sulfonylureas are used concomitantly with azole antifungal agents that inhibit CYP450 2C9.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/176407/', '', 'Caspofungin, Micafungin, Amphotericin B, Oteseconazole, Ketoconazole, Flucytosine, Anidulafungin', 'Lixisenatide, Albiglutide, Guar gum, Canagliflozin, Pramlintide, Sitagliptin, Alogliptin, Liraglutide, Ertugliflozin, Rosuvastatin, Semaglutide, More', 1767369485);
