INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (176259, 'Voriconazole', 'Telaprevir', 'Moderate', 'Coadministration with voriconazole may increase the plasma concentrations of telaprevir. The mechanism involves voriconazole inhibition of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of telaprevir. The interaction has not been specifically studied with voriconazole, but has been reported with another azole antifungal agent.', 'DDInter', 'Voriconazole should not be administered to patients receiving telaprevir unless the benefits are anticipated to outweigh the risks.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/176469/', '', 'Fluconazole, Caspofungin, Amphotericin B, Micafungin, Oteseconazole, Flucytosine, Anidulafungin', 'Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Lenacapavir, Velpatasvir, Oseltamivir, Brincidofovir, Stavudine, Famciclovir, Darunavir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (176260, 'Voriconazole', 'Terbutaline', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/176470/', '', 'Caspofungin, Micafungin, Amphotericin B, Itraconazole, Miconazole, Oteseconazole, Flucytosine, Anidulafungin', 'Ephedrine, Mometasone furoate, Umeclidinium, Aclidinium, Glycopyrronium, Cromoglicic acid, Ipratropium, Epinephrine, Beclomethasone dipropionate', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (176261, 'Voriconazole', 'Terfenadine', 'Major', 'Coadministration with azole antifungal agents may significantly increase the plasma concentrations of terfenadine. The mechanism is azole inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of terfenadine. High plasma levels of terfenadine have been associated with prolongation of the QT interval on the ECG; ventricular arrhythmias including ventricular tachycardia and torsade de pointes; cardiac arrest; and sudden death. Within the azole class, ketoconazole and itraconazole are considered the most potent inhibitors, while fluconazole is comparatively weak and generally causes clinically significant interactions with CYP450 3A4 substrates only at dosages of 200 mg/day or more.', 'DDInter', 'The use of terfenadine with most azole antifungal agents, including fluconazole at multiple doses of 400 mg or higher, is considered contraindicated. Some authorities consider concomitant administration of terfenadine and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole (AU). Loratadine, cetirizine, or fexofenadine may be safer alternatives during therapy with azole antifungal agents.', 'Metabolism', 'The use of terfenadine with most azole antifungal agents, including fluconazole at multiple doses of 400 mg or higher, is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/176471/', '', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Cetirizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, More', 'Caspofungin, Micafungin, Amphotericin B, Oteseconazole, Flucytosine, Anidulafungin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (176262, 'Voriconazole', 'Testosterone (topical)', 'Moderate', 'Coadministration with voriconazole may increase the plasma concentrations of drugs that are substrates of CYP450 2C19, 2C9, and/or 3A4. The mechanism is decreased clearance due to inhibition of those isoenzymes by voriconazole. Increased plasma levels and/or pharmacologic effects of drugs such as cyclosporine, sirolimus, tacrolimus (3A4 substrates), and warfarin (2C9 substrate) have been reported during coadministration with voriconazole.', 'DDInter', 'Caution is advised if voriconazole must be used concomitantly with medications that undergo metabolism by CYP450 2C19, 2C9 and/or 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever voriconazole is added to or withdrawn from therapy. The manufacturer specifically recommends that a dosage reduction be considered for benzodiazepines, statins, long-acting opiates, and vinca alkaloids that are metabolized by the affected isoenzymes when used with voriconazole.', 'Metabolism', 'Caution is advised if voriconazole must be used concomitantly with medications that undergo metabolism by CYP450 2C19, 2C9 and/or 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/176472/', '', 'Caspofungin, Micafungin, Amphotericin B, Miconazole, Posaconazole, Oteseconazole, Ketoconazole, Flucytosine, Anidulafungin', 'Methyltestosterone, Fluoxymesterone, Prasterone, Methyltestosterone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (176263, 'Voriconazole', 'Thioridazine', 'Major', 'Thioridazine can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, certain agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive parasympatholytic and central nervous system-depressant effects when used in combination with thioridazine. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', 'DDInter', 'Coadministration of thioridazine with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/176473/', '', 'Micafungin, Caspofungin, Itraconazole, Miconazole, Oteseconazole, Flucytosine, Anidulafungin', 'Loxapine, Molindone, Thiothixene', 1767369485);
