INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (176878, 'Aminophylline', 'Zileuton', 'Moderate', 'Coadministration with zileuton may significantly increase the plasma concentrations of theophylline. The proposed mechanism is zileuton inhibition of theophylline metabolism via CYP450 1A2.', 'DDInter', 'Caution is advised if theophylline is prescribed in combination with zileuton, particularly in the elderly and patients with chronic obstructive pulmonary disease, congestive heart failure or cirrhosis, as these patients generally have lower theophylline clearance rates and may be particularly vulnerable to theophylline toxicity. When zileuton is initiated in patients receiving theophylline, the theophylline dosage should be reduced by approximately one-half and theophylline levels closely monitored. Similarly, when initiating therapy with theophylline in patients receiving zileuton, the maintenance dosage and/or dosing interval of theophylline should be adjusted accordingly and guided by serum theophylline levels. Patients should be advised to notify their physician if they experience signs and symptoms suggestive of theophylline toxicity such as nausea, vomiting, diarrhea, headache, tremor, irritability, insomnia, seizures, palpitation, or arrhythmia.', 'Metabolism', 'Caution is advised if theophylline is prescribed in combination with zileuton, particularly in the elderly and patients with chronic obstructive pulmonary disease, congestive heart failure or cirrhosis, as these patients generally have lower theophylline clearance rates and may be particularly vulnerable to theophylline toxicity.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/177089/', '', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Resli