INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (177824, 'Entrectinib', 'Tacrine', 'Moderate', 'Acetylcholinesterase inhibitors may have vagotonic effects on the sinoatrial and atrioventricular nodes, which occasionally manifest as bradycardia or heart block (<2%). Because bradycardia is a risk factor for torsade de pointes, a theoretical risk exists when combined with agents that prolong the QT interval.', 'DDInter', 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat.', 'Synergism', 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178035/', '', 'Memantine, Ginkgo biloba, Aducanumab', 'Imatinib, Dabrafenib, Acalabrutinib, Dacomitinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Selumetinib, Cobimetinib, Ibrutinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (177825, 'Entrectinib', 'Telaprevir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of entrectinib, which is primarily metabolized by the isoenzyme. Increased exposure to entrectinib may increase the risk and/or severity of adverse effects such as cognitive impairment, mood disorders, dizziness, sleep disturbances, liver enzyme elevations, hyperuricemia, congestive heart failure, edema, myocarditis, QT prolongation, vision problems, anemia, and neutropenia.', 'DDInter', 'Concomitant use of entrectinib with potent (or moderate) CYP450 3A4 inhibitors should be avoided when possible. If coadministration is necessary, the dosage of entrectinib should be reduced to 100 mg once daily for adults and pediatrics patients (12 years and older) with a body surface area (BSA) greater than 1.5 m2. Following discontinuation, and after an appropriate washout period of the CYP450 3A4 inhibitor (i.e., 3 to 5 half-lives), the entrectinib dosage taken prior to initiating the CYP450 3A4 inhibitor may be resumed. For pediatric patients with a body surface area of 1.5 m2 or less, concomitant use of entrectinib with potent (or moderate) CYP450 3A4 inhibitors should be avoided.', 'Metabolism', 'Concomitant use of entrectinib with potent (or moderate) CYP450 3A4 inhibitors should be avoided when possible.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178036/', '', 'Lamivudine, Tenofovir alafenamide, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Stavudine, Elbasvir, More', 'Tucatinib, Alpelisib, Lenvatinib, Trametinib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (177826, 'Entrectinib', 'Terbutaline', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178037/', '', 'Ephedrine, Mometasone furoate, Umeclidinium, Aclidinium, Glycopyrronium, Mometasone, Epinephrine, Fluticasone, Cromoglicic acid, Ipratropium, Tiotropium, Beclomethasone dipropionate', 'Imatinib, Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Selumetinib, Cobimetinib, More', 1767369485);
